def check_balance(w3, token, address):
    contract = w3.eth.contract(address=Web3.to_checksum_address(token),abi=token_abi)
    balance = contract.functions.balanceOf(address).call()
    return balance




def sleep_indicator(time):
    for i in tqdm(range(time), desc='жду', bar_format="{desc}: {n_fmt}c /{total_fmt}c {bar}", colour='green'):
        t.sleep(1)

def check_allowance(w3, token, address, spender):
    contract = w3.eth.contract(address=Web3.to_checksum_address(token),abi=token_abi)
    allowance = contract.functions.allowance(address, Web3.to_checksum_address(spender)).call()
    return allowance

def approve_token(w3,account, address, token, spender, amount):
    while True:
        contract = w3.eth.contract(address=token, abi=token_abi)
        try:
            allowance = check_allowance(w3,token, address,spender)
            if amount > allowance:
                logger.info(f'{address} - апруваю usdc...')
                tx = contract.functions.approve(spender, amount * 5).build_transaction({
                    'from': address,
                    'nonce': w3.eth.get_transaction_count(address),
                    'gasPrice': w3.eth.gas_price,
                    'gas': contract.functions.approve(spender, amount * 5).estimate_gas(
                        {'from': address, 'nonce': w3.eth.get_transaction_count(address)})
                })
                sign = account.sign_transaction(tx)
                hash = w3.eth.send_raw_transaction(sign.rawTransaction)
                status = check_status_tx(hash,address,w3)
                sleep_indicator(5)
                if status == 1:
                    logger.success(f'{address} - апрувнул usdc : https://optimistic.etherscan.io/tx/{w3.to_hex(hash)}...')
                    sleep_indicator(random.randint(5, 20))
                    break
                else:
                    logger.info(f'{address}- не смог апрувнуть : https://optimistic.etherscan.io/tx/{w3.to_hex(hash)}...')

            else:
                logger.info(f'{address} - уже апрувнуто...')
                sleep_indicator(5)
                break
        except Exception as e:
            logger.error(f'{address} - {e}...')
            t.sleep(2)


def check_status_tx(tx_hash,address,w3):

        logger.info(f'{address} - жду подтверждения транзакции...')

        while True:
            try:
                status = w3.eth.get_transaction_receipt(tx_hash)['status']
                if status in [0, 1]:
                    return status
                t.sleep(1)
            except Exception as error:
                t.sleep(1)

def get_quote(address,amount):
    ua = UserAgent()
    ua = ua.random
    headers = {
        'authority': 'api.uniswap.org',
        'accept': '*/*',
        'accept-language': 'ru-RU,ru;q=0.9,en-US;q=0.8,en;q=0.7',
        'origin': 'https://app.uniswap.org',
        'referer': 'https://app.uniswap.org/',
        'sec-ch-ua': '"Not.A/Brand";v="8", "Chromium";v="114", "Google Chrome";v="114"',
        'sec-ch-ua-mobile': '?0',
        'sec-ch-ua-platform': '"Windows"',
        'sec-fetch-dest': 'empty',
        'sec-fetch-mode': 'cors',
        'sec-fetch-site': 'same-site',
        'user-agent': ua,
    }

    params = {
        'protocols': 'v2,v3,mixed',
        'tokenInAddress': 'ETH',
        'tokenInChainId': '10',
        'tokenOutAddress': '0x7F5c764cBc14f9669B88837ca1490cCa17c31607',
        'tokenOutChainId': '10',
        'amount': amount,
        'type': 'exactOut',
    }

    try:
        response = requests.get('https://api.uniswap.org/v1/quote', params=params, headers=headers)
        if response.status_code == 200:
            res = json.loads(response.text)
            return int(res['quote'])
    except Exception as e:
        logger.error(f'{address} - {e}')
        t.sleep(5)


def uniswap(privatekey,w3,amount):
    account = w3.eth.account.from_key(privatekey)
    address = account.address
    uniswap_address = w3.to_checksum_address("0x68b3465833fb72a70ecdf485e0e4c7bd8665fc45")
    uniswap = w3.eth.contract(address=uniswap_address, abi=uniswap_abi)

    # timestamp
    now = dt.datetime.now()
    mins = 10
    timestamp = int((now + dt.timedelta(minutes=mins)).timestamp())

    weth = Web3.to_checksum_address('0x4200000000000000000000000000000000000006')
    token = Web3.to_checksum_address('0x7F5c764cBc14f9669B88837ca1490cCa17c31607')
    amount_dec = int(amount*10**6)
    amount_eth = get_quote(address,amount_dec)
    data = uniswap.encodeABI(fn_name='exactInputSingle',
                             args=[(weth, token, 500, address, amount_eth, int(amount_dec*0.99), 0)])
    nonce = w3.eth.get_transaction_count(address)
    try:
        while True:
            tx = uniswap.functions.multicall(
                timestamp,
                [data]
            ).build_transaction(
                {
                    'from': address,
                    'nonce': nonce,
                    'value': amount_eth,
                    'gas': uniswap.functions.multicall(
                        timestamp,
                        [data]
                    ).estimate_gas(
                        {
                            'from': address,
                            'nonce': nonce,
                            'value': amount_eth
                        }
                    ),
                    'gasPrice': w3.eth.gas_price
                }
            )
            sign = account.sign_transaction(tx)
            hash = w3.eth.send_raw_transaction(sign.rawTransaction)

            logger.info(f'{address} - покупаю {amount} usdc на Uniswap...')

            status = check_status_tx(hash, address, w3)
            sleep_indicator(5)

            if status == 1:
                logger.success(f'{address} - купил {amount} usdc : https://optimistic.etherscan.io/tx/{w3.to_hex(hash)} на Uniswap...')


                sleep_indicator(random.randint(1, 25))
                break
            else:
                logger.info(f'{address} - транза неуспешна : https://optimistic.etherscan.io/tx/{w3.to_hex(hash)}...')

    except Exception as e:
        error_message = str(e)
        if "insufficient funds for gas * price + value" in error_message:
            logger.error(f'{address} - нет баланса eth...')
            break
        else:

            logger.error(f'{address} - {e}...')
        t.sleep(2)

def send_and_get(privatekey, amount,delay):
    w3 = Web3(Web3.HTTPProvider('https://optimism.publicnode.com'))
    account = w3.eth.account.from_key(privatekey)
    address = account.address
    usdc = Web3.to_checksum_address('0x7F5c764cBc14f9669B88837ca1490cCa17c31607')
    to = Web3.to_checksum_address('0x789d425a45557a9743029f937a3ba9aac0827008')
    while True:
        balance = check_balance(w3,usdc,address)
        if balance>int(amount*10**6):
            approve_token(w3,account,address,usdc,to,int(amount*10**6))
            logger.info(f'{address} - начинаю отправку {amount} usdc...')
            try:
                data = '0x160e3f3d'
                data_ = (encode(['uint256'], [int(amount*10**6)])).hex()
                data = data + data_
                tx = {
                    "from": address,
                    "to": to,
                    "value": 0,
                    "nonce": w3.eth.get_transaction_count(address),
                    "chainId": w3.eth.chain_id,
                    "gasPrice": w3.eth.gas_price,
                    "data": data,
                }
                gas = w3.eth.estimate_gas(tx)
                tx["gas"] = gas
                sign = account.sign_transaction(tx)
                hash = w3.eth.send_raw_transaction(sign.rawTransaction)
                status = check_status_tx(hash,address,w3)
                sleep_indicator(5)
                if status == 1:
                    logger.success(f'{address} - успешно отправил {amount} usdc и получил нфт ...')
                    sleep_indicator(random.randint(delay[0],delay[1]))
                    return address, 'success'
            except Exception as e:
                logger.error(f'{address} - {e}')
                sleep_indicator(random.randint(delay[0], delay[1]))
                return address,'error'
        else:
            logger.info(f'{address} - нет {amount} usdc, иду покупать...')
            uniswap(privatekey,w3,amount)


def main():
    print(f'\n{" "*32}автор - https://t.me/iliocka{" "*32}\n')
    with open("keys.txt", "r") as f:
        keys = [row.strip() for row in f]
    for key in keys:
        delay = (0,20)  #задержка между кошелями
        amount = random.uniform(0.1,0.15)   # от и до рандомная сумма , минимум 0.1 usdc!!!
        res = send_and_get(key,amount,delay)
        wallets.append(res[0]), results.append(res[1])
    res = {'address': wallets, 'result': results}
    df = pd.DataFrame(res)
    df.to_csv('results.csv', index=False)
    logger.success('Минетинг закончен...')

if __name__ == '__main__':
    main()



